Scripts.OnLoad:
local grab = { CharacterMicroButton, SpellbookMicroButton, TalentMicroButton, AchievementMicroButton, QuestLogMicroButton, SocialsMicroButton, PVPMicroButton, LFDMicroButton, MainMenuMicroButton, HelpMicroButton }
this.OnMAHook = function(this) if KPack.MA:IsFrameHooked("MicroButtonsMover") then KPack.MA:ResetFrame("MicroButtonsMover") end this.attachedChildren = {}
--KPack.MA:LockPoint(CharacterMicroButton)
local b, pb for i in pairs(grab) do b = grab[i]
b:ClearAllPoints() if i == 1 then b:SetPoint("TOPLEFT", this, "TOPLEFT", 0, 24) else grab[i]:SetPoint("TOPLEFT", pb, "BOTTOMLEFT", 0, 24) end b.MAParent = this KPack.MA:LockPoint(b) table.insert(this.attachedChildren, b) pb = b end this:Show() end this.OnMAPreReset = function (this) --KPack.MA:UnlockPoint(CharacterMicroButton) local p = _G["MicroButtonsMover"] if this.attachedChildren then for i, b in pairs(this.attachedChildren) do b.MAParent = p KPack.MA:UnlockPoint(b) end end end this.OnMAPostReset = function(this) CharacterMicroButton:ClearAllPoints() CharacterMicroButton:SetPoint("BOTTOMLEFT", "MainMenuBarArtFrame", "BOTTOMLEFT", 546, 2)
local b, pb for i in pairs(grab) do b = grab[i] if i > 1 then b:ClearAllPoints() b:SetPoint("BOTTOMLEFT", pb, "BOTTOMRIGHT", -2, 0) end pb = b end this:Hide() end
